definition.script:
pipeline {
    agent {
        docker {
            image "alpine"
        }
    }

    environment {
        WORKDIR = sh(script: 'cygpath -u "${PWD}"', returnStdout: true).trim()
    }

    stages {
        stage('access-google-home') {
            steps {
                script {
                    def unixPath = sh(script: 'cygpath -u "${PWD}"', returnStdout: true).trim()
                    docker.image('alpine').inside("--workdir ${unixPath}") {
                        sh "wget www.google.com"
                    }
                }
            }
        }
    }
}
